  function getCompanyIdForCompanyInformation (companyPatentId, callback) {
    connection.query("SELECT company_id FROM company_patent WHERE id = ?", [companyPatentId], function (err, results) {
      if (err) {
        callback(err,null);
        return;
      } else {
         callback(null, results)
      }
    })
